import React from "react";
import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";

// TypeScriptのための型定義を追加
interface RouteParams {
  userId: string;
}

interface User {
  [index: number]: string; // これは仮の型定義です。実際のユーザー情報の構造に合わせてください。
}

interface State {
  auth: {
    userData: User[];
  };
}

// ここで型注釈を追加します。
const selectUserById = (state: State, userId: string) =>
  state.auth.userData.find((user: User) => user[0] === userId);

function UserProfile() {
  // useParamsの型をRouteParamsとして指定
  const { userId } = useParams<RouteParams>();

  // useSelectorに渡す関数の引数の型をStateとして指定
  const user = useSelector((state: State) => selectUserById(state, userId));

  // 以下は変更なし...
  if (!user) {
    return <div>Loading user data...</div>;
  }

  // ここにユーザーの詳細情報を表示するコードを書きます
  return (
    <div>
      <h1>User Profile for {user[1]}</h1>
      <p>ID: {user[0]}</p>
      <p>Name: {user[1]}</p>
      <p>Email: {user[2]}</p>
      {/* user配列の中の他の情報もこのように表示できます */}
    </div>
  );
}

export default UserProfile;
