import React, { useState } from "react";
import "./SideMenu.css";

// propsでprofileImageUrlを受け取る
function SideMenu({ profileImageUrl }: { profileImageUrl: string }) {
  const [isOpen, setIsOpen] = useState(false);

  const menuWidth = 250; // ここでサイドメニューの幅を定義

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div>
      {/* メニュートグルボタン */}
      <button
        className="menuToggle"
        onClick={toggleMenu}
        style={{
          left: isOpen ? `${menuWidth}px` : "0", // isOpenがtrueならメニューの幅の位置に、falseなら0の位置に
        }}
      >
        {isOpen ? "Close" : "Menu"}
      </button>

      {/* サイドメニュー */}
      <div
        className="sideMenu"
        style={{
          width: `${menuWidth}px`, // メニューの幅
          transform: isOpen ? "translateX(0)" : `translateX(-${menuWidth}px)`, // isOpenがtrueなら表示、falseなら隠す
        }}
      >
        {/* メニューコンテンツ */}
        {/* プロフィール画像 */}
        <div className="profileImageContainer">
          <img className="memberFhoto" src={profileImageUrl} alt="Profile" />
        </div>
      </div>
    </div>
  );
}

export default SideMenu;
