function UserProfile() {
  const { userId } = useParams<Params>(); // URLからuserIdを取得
  const [user, setUser] = useState<string[] | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // fetch処理...
        const response = await fetch(
          "https://sheets.googleapis.com/v4/spreadsheets/1yajpuM9YfEqlHgGbDxYVQOcHrFfJYoTho1b1qoOME6Y/values/%E3%82%B7%E3%83%BC%E3%83%881?key=AIzaSyBoGN_ggnHtfZrcL1FX81HSWzQirXL8eyg"
        );
        const data = await response.json();

        // ユーザーデータが見つかるかチェック
        const userData = data.values.find((row: string[]) => row[0] === userId);
        if (userData) {
          setUser(userData);
        } else {
          // ユーザーデータが見つからない場合の処理
          setError("User not found");
        }
      } catch (e) {
        // エラー処理
        setError("An error occurred while fetching user data");
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, [userId]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  // user[1], user[2], ... を使ってユーザーの詳細情報を表示
  return (
    <div>
      <h1>User Profile</h1>
      {user ? (
        <>
          <p>Name: {user[1]}</p>
          {/* その他のユーザー情報 */}
        </>
      ) : (
        <p>User not found</p>
      )}
    </div>
  );
}

export default UserProfile;
