import React, { useState } from "react";
import { useSelector } from "react-redux";
import { RootState } from "../store";
import "./SideMenu.css";
import CloseIcon from "@mui/icons-material/Close";
import PersonIcon from "@mui/icons-material/Person";
import EditIcon from "@mui/icons-material/Edit";

// propsでprofileImageUrlを受け取る
function SideMenu({ profileImageUrl }: { profileImageUrl: string }) {
  const [isOpen, setIsOpen] = useState(false);

  const employeeData = useSelector((state: RootState) => state.auth.userData);

  const menuWidth = 300; // ここでサイドメニューの幅を定義

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };
  const employeeNumber = employeeData ? employeeData[0] : "";
  const employeeName = employeeData ? employeeData[1] : "";
  const employeeFurigana = employeeData ? employeeData[2] : "";

  return (
    <>
      {/* オーバーレイ */}
      {isOpen && (
        <div
          className="overlay"
          onClick={toggleMenu} // オーバーレイをクリックするとメニューが閉じる
        />
      )}

      <div className="menuToggleArea">
        {/* メニュートグルボタン */}
        <button
          className="menuToggle"
          onClick={toggleMenu}
          style={{
            left: isOpen ? `${menuWidth}px` : "0", // isOpenがtrueならメニューの幅の位置に、falseなら0の位置に
          }}
        >
          {isOpen ? (
            <p className="toogleText">
              <CloseIcon />
              CLOSE
            </p>
          ) : (
            <p className="toogleText">
              <PersonIcon />
              MY PAGE
            </p>
          )}
        </button>

        {/* サイドメニュー */}
        <div
          className="sideMenu"
          style={{
            width: `${menuWidth}px`, // メニューの幅
            transform: isOpen ? "translateX(0)" : `translateX(-${menuWidth}px)`, // isOpenがtrueなら表示、falseなら隠す
          }}
        >
          {/* メニューコンテンツ */}
          <div className="SideProfileContainer">
            <div className="editBtn">
              <EditIcon />
            </div>
            <div className="profileImageContainer">
              <img
                className="memberFhoto"
                src={profileImageUrl || "/Noimage.png"}
                alt="Profile"
              />
              <p className="employeeNumber">{employeeNumber}</p>
              <p className="employeeName">{employeeName}</p>
              <p className="employeeFurigana">{employeeFurigana}</p>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default SideMenu;
