import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

type Params = {
  userId: string;
};

type User = string[]; // ここでユーザーの型を定義します

function UserProfile() {
  const { userId } = useParams<Params>();
  const [user, setUser] = useState<User | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch(
          "https://sheets.googleapis.com/v4/spreadsheets/1yajpuM9YfEqlHgGbDxYVQOcHrFfJYoTho1b1qoOME6Y/values/%E3%82%B7%E3%83%BC%E3%83%881?key=AIzaSyBoGN_ggnHtfZrcL1FX81HSWzQirXL8eyg"
        );
        const result = await response.json();

        if (!result.values) {
          throw new Error("No data available");
        }

        // useParamsで取得したuserIdを数値に変換して比較
        const userData = result.values.find(
          (row: User) => parseInt(row[0], 10) === parseInt(userId, 10)
        );

        if (userData) {
          setUser(userData);
        } else {
          setError("User not found.");
          setUser(null);
        }
      } catch (err) {
        if (err instanceof Error) {
          setError(err.message);
        } else {
          setError("An unknown error occurred");
        }
        setUser(null);
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, [userId]);

  if (loading) return <div>Loading user data...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!user) return <div>User not found</div>;

  return (
    <div>
      <h1>User Profile</h1>
      <p>ID: {user[0]}</p>
      <p>Name: {user[1]}</p>
      {/* その他のユーザー情報を表示 */}
    </div>
  );
}

export default UserProfile;
