import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

// useParamsによって返されるパラメーターの型を定義します。
type Params = {
  userId: string;
};

function UserProfile() {
  const { userId } = useParams<Params>(); // URLからuserIdを取得
  // userステートの型を明示します。もしユーザーデータが文字列の配列であれば `string[]` とします。
  // ユーザーデータの型が不明または複雑な場合は、それに合った型を定義するか `any[]` としてください。
  const [user, setUser] = useState<string[] | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserData = async () => {
      // ...
      // fetch処理
      // ...
      const data = await Response.json();
      // rowの型を明示的に指定します。ここでは `string[]` としましたが、
      // 実際のデータ構造に合わせてください。
      const userData = data.values.find((row: string[]) => row[0] === userId);
      setUser(userData);
      setLoading(false);
    };

    fetchUserData().catch((error) => {
      // エラーハンドリング
      setLoading(false);
    });
  }, [userId]);

  // user[1], user[2], ... を使ってユーザーの詳細情報を表示
  return (
    <div>
      <h1>User Profile</h1>
      <p>Name: {user[1]}</p>
      {/* その他のユーザー情報 */}
    </div>
  );
}

export default UserProfile;
