import React, { useState } from "react";
import { useSelector } from "react-redux";
import { RootState } from "../store";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import SaveIcon from "@mui/icons-material/Save";

function SideMenu() {
  const [isEditing, setIsEditing] = useState(false);
  const [editedComment, setEditedComment] = useState("");
  const profile = useSelector((state: RootState) => state.profile);

  const handleEditClick = () => {
    setIsEditing(true);
    setEditedComment(profile.employeeComment);
  };

  const handleSaveClick = async () => {
    const employeeNumber = profile.employeeNumber; // ログインしているユーザーの社員番号
    const spreadsheetId = "1yajpuM9YfEqlHgGbDxYVQOcHrFfJYoTho1b1qoOME6Y"; // スプレッドシートID
    const apiKey = "your_api_key_here"; // APIキーを入力してください。
    const range = "シート1"; // シート名

    // スプレッドシートAPIのURL
    const url = `https://sheets.googleapis.com/v4/spreadsheets/${spreadsheetId}/values/${range}?key=${apiKey}`;

    try {
      const response = await fetch(url);
      const data = await response.json();
      const rows = data.values;

      // 社員番号で行を検索
      const rowIndex = rows.findIndex((row) => row[0] === employeeNumber);
      if (rowIndex === -1) {
        alert("User not found");
        return;
      }

      // 更新するセルの範囲
      const cellRange = `${range}!I${rowIndex + 1}`;

      // スプレッドシートを更新するURL
      const updateUrl = `https://sheets.googleapis.com/v4/spreadsheets/${spreadsheetId}/values/${cellRange}?valueInputOption=USER_ENTERED&key=${apiKey}`;

      const updateResponse = await fetch(updateUrl, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          values: [[editedComment]], // 更新するデータ
        }),
      });

      if (!updateResponse.ok) {
        throw new Error("Failed to update spreadsheet");
      }

      setIsEditing(false);
      alert("Comment updated successfully.");
    } catch (error) {
      console.error("Error:", error);
      alert("Failed to update comment.");
    }
  };

  return (
    <div className="sideMenu">
      {/* ... 既存のマークアップ ... */}
      <div className="profileSection">
        {isEditing ? (
          <>
            <TextField
              fullWidth
              multiline
              variant="outlined"
              value={editedComment}
              onChange={(e) => setEditedComment(e.target.value)}
            />
            <Button
              startIcon={<SaveIcon />}
              onClick={handleSaveClick}
              color="primary"
            >
              Save
            </Button>
          </>
        ) : (
          <>
            <p>{profile.employeeComment}</p>
            <Button onClick={handleEditClick}>Edit</Button>
          </>
        )}
      </div>
      {/* ... 既存のマークアップ ... */}
    </div>
  );
}

export default SideMenu;
