import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

type Params = {
  userId: string;
};

function UserProfile() {
  const { userId } = useParams<Params>();
  const [user, setUser] = useState<string[] | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserData = async () => {
      setLoading(true); // ローディング状態の開始
      try {
        // APIからのレスポンスを待ちます。
        const response = await fetch(
          "https://sheets.googleapis.com/v4/spreadsheets/1yajpuM9YfEqlHgGbDxYVQOcHrFfJYoTho1b1qoOME6Y/values/%E3%82%B7%E3%83%BC%E3%83%881?key=AIzaSyBoGN_ggnHtfZrcL1FX81HSWzQirXL8eyg"
        );
        // レスポンスのJSONを取得します。
        const data = await response.json();
        // データからuserIdに該当するユーザー情報を検索します。
        const userData = data.values.find((row: string[]) => row[0] === userId);
        setUser(userData); // ユーザーデータをセットします。
      } catch (error) {
        console.error("An error occurred while fetching user data:", error);
      } finally {
        setLoading(false); // ローディング状態の終了
      }
    };

    fetchUserData();
  }, [userId]);

  if (loading) return <div>Loading user data...</div>;
  if (!user) return <div>User not found</div>;

  return (
    <div>
      <h1>User Profile</h1>
      {/* userがnullではないことが保証されているので、nullチェックをせずにアクセスします */}
      <p>Name: {user && user[1]}</p>
      {/* その他のユーザー情報 */}
    </div>
  );
}

export default UserProfile;
