import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

// パラメータの型を定義します（userIdは数値として扱います）
type Params = {
  userId: string; // URLから取得されるのは常に文字列ですが、比較の際に数値に変換します。
};

// ユーザーデータの型を定義します
// ここではユーザーデータを文字列の配列として扱いますが、必要に応じて適宜型を調整してください
type User = string[];

function UserProfile() {
  const { userId } = useParams<Params>();
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    async function fetchData() {
      try {
        // Google Sheets APIからデータをフェッチします
        const response = await fetch(
          "https://sheets.googleapis.com/v4/spreadsheets/SPREADSHEET_ID/values/シート1?key=YOUR_API_KEY"
        );
        const result = await response.json();

        // 数値型に変換してから比較する
        const userData = result.values.find(
          (row: User) => parseInt(row[0], 10) === parseInt(userId, 10)
        );

        if (userData) {
          setUser(userData);
        } else {
          setError("User not found.");
          setUser(null);
        }
      } catch (err) {
        if (err instanceof Error) {
          setError(err.message);
        } else {
          setError("An unknown error occurred");
        }
        setUser(null);
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, [userId]);

  // ローディング中の表示
  if (loading) return <div>Loading user data...</div>;

  // エラーがあった場合の表示
  if (error) return <div>Error: {error}</div>;

  // ユーザーが見つからない場合の表示
  if (!user) return <div>User not found</div>;

  // ユーザーデータがある場合の表示
  return (
    <div>
      <h1>User Profile</h1>
      <p>ID: {user[0]}</p>
      <p>Name: {user[1]}</p>
      {/* 他のユーザー情報をここに表示 */}
    </div>
  );
}

export default UserProfile;
